import { Input } from "@/components/ui/input";

export const inputSizes = ["sm", "md", "lg"] as const;

export const InputSizeExample = () => {
    return (
        <div className="flex flex-col gap-4 w-full max-w-sm">
            {inputSizes.map((size) => (
                <Input key={size} size={size} placeholder={size} />
            ))}
        </div>
    );
};
