import { Textarea, TextareaInput, TextareaLabel } from "@/components/ui/textarea";

export const inputVariants = [
  "default",
  "faded",
  "bordered",
  "underline",
] as const;

export const TextareaLabelExample = () => {
  return (
    <div className="grid grid-cols-2 gap-4 w-full max-w-120">
      {inputVariants.map((variant) => (
        <Textarea key={variant} variant={variant} className="gap-0">
          <TextareaLabel htmlFor={variant}>Bio</TextareaLabel>
          <TextareaInput id={variant} placeholder="Please enter your Bio..." />
        </Textarea>
      ))}
    </div>
  );
};
